/**
 * Start a server with BrowserSync to preview the site in
 * @param done
 */
function startServer(done) {
    browser.init({
        server: config.paths.dist.path,
        port: config.development.server.port
    });
    done();
}

/**
 * Reload the browser with BrowserSync
